import React, { Component } from "react";
import List from "./List.js";

class TaskFrom2 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: [],
      isDisplayForm: ""
    };
  }

  DataTaskFrom = data => {
    console.log(data);
    var { tasks } = this.state;
    if (data.id === "") {
      data.id = this.AutoId();
      tasks = tasks.concat(data);
    } else {
      var index = this.findIndex(data.id);
      tasks[index] = data;
    }
    this.setState({
      tasks: tasks,
    });
    localStorage.setItem("tasks", JSON.stringify(tasks));
  };

  /* Random ID */
  rd4() {
    return Math.floor((1 + Math.random()) * 0x10000)
      .toString(16)
      .substring(1);
  }
  AutoId = () => {
    return this.rd4() + this.rd4() + "_" + this.rd4() + this.rd4();
  }; //********* */
  /**/
  ChangeDisplay = () => {
    this.setState({});
  };
  render() {
    var { isDisplayForm } = this.props;
    var { tasks } = this.pro;
    var Element = tasks.map((task, index) => {
      return <List key={task.id} index={index} task={task} />;
    });
    return (
      <div className={isDisplayForm ? "taskfrom2" : "taskfrom2-2"}>
        <div className="tablets">
          <table className="ui celled table">
            <thead className="">
              <tr className="">
                <th className="">STT</th>
                <th className="">Name</th>
                <th className="">Status</th>
                <th className="">Action</th>
              </tr>
            </thead>
            <tbody className="">
              <tr className="">
                <td className="">
                  <div className="ui ribbon label">Null</div>
                </td>
                <td className="">
                  <div className="ui focus input">
                    <input type="text" placeholder="Name..." />
                  </div>
                </td>
                <td>
                  <div className="field">
                    <select>
                      <option value="volvo">Volvo</option>
                      <option value="saab">Saab</option>
                    </select>
                  </div>
                </td>
                <td className="" />
              </tr>
              {Element}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default TaskFrom2;
