import React, { Component } from "react";
import List from "./List.js";

class TaskFrom2 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: [],
      isDisplayForm: ""
    };
  }

  /* Random ID */
  rd4() {
    return Math.floor((1 + Math.random()) * 0x10000)
      .toString(16)
      .substring(1);
  }
  AutoId = () => {
    return this.rd4() + this.rd4() + "_" + this.rd4() + this.rd4();
  }; //********* */
  /**/
  ChangeDisplay = () => {
    this.setState({});
  };
  render() {
    var { isDisplayForm } = this.props;
    var { tasks } = this.props;
    var Element = tasks.map((task, index) => {
      return (
        <List
        //truyen nguoc ra app.js
          OnUpdateStatus={this.props.OnUpdateStatus}
          Delected={this.props.Delected}
          Update={this.props.Update}
          //***** */
          key={task.id}
          index={index}
          task={task}
        />
      );
    });
    return (
      <div className={isDisplayForm ? "taskfrom2" : "taskfrom2-2"}>
        <div className="tablets">
          <table className="ui celled table">
            <thead className="">
              <tr className="">
                <th className="">STT</th>
                <th className="">Name</th>
                <th className="">Status</th>
                <th className="">Action</th>
              </tr>
            </thead>
            <tbody className="">
              <tr className="">
                <td className="">
                  <div className="ui ribbon label">Null</div>
                </td>
                <td className="">
                  <div className="ui focus input">
                    <input type="text" placeholder="Name..." />
                  </div>
                </td>
                <td>
                  <div className="field">
                    <select>
                      <option value="volvo">Volvo</option>
                      <option value="saab">Saab</option>
                    </select>
                  </div>
                </td>
                <td className="" />
              </tr>
              {Element}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default TaskFrom2;
