import React, { Component } from "react";

class TaskFrom extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      status: false
    };
  }

  onChange=(event)=>{
    var name = event.target
  }
  HidenFrom = () => {
    this.props.HidenFrom();
  };

  render() {
    return (
      <div className="taskfrom">
        <div className="ui segment">
          ADD JOBS{" "}
          <button onClick={this.HidenFrom} className="ui red button">
            Hide
          </button>
        </div>
        <form className="ui form">
          <div className="field">
            <label>Name</label>
            <input
              placeholder="Name"
              name="name"
              value={this.state.name}
              onChange={this.onChange}
            />
          </div>
          <div className="field">
            <label>Status</label>
            <select
              name="status"
              value={this.state.status}
              onChange={this.onChange}
            >
              <option value={true}>Active</option>
              <option value={false}>Hiden</option>
            </select>
          </div>

          <button type="submit" className="ui primary button">
            Save
          </button>
          <button type="submit" className="ui secondary button">
            Remove
          </button>
        </form>
      </div>
    );
  }
}

export default TaskFrom;
