import React, { Component } from "react";
import TaskFrom from "./components/TaskFrom";
import TaskFrom2 from "./components/TaskFrom2";
import "./App.css";
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isDisplayForm: false,
      tasks: []
    };
  }
  ChangeDisplay = () => {
    this.setState({
      isDisplayForm: !this.state.isDisplayForm
    });
  };
  HidenFrom = () => {
    this.setState({
      isDisplayForm: false
    });
  };
  //luu data tu taskfrom truyen qua
  DataTaskFrom = data => {
    data.id = this.AutoId();
    console.log(data);
    var { tasks } = this.state;
    tasks.push(data);
    this.setState({
      tasks: tasks
    });
    localStorage.setItem("tasks", JSON.stringify(tasks));
  };

  /* Random ID */
  rd4() {
    return Math.floor((1 + Math.random()) * 0x10000)
      .toString(16)
      .substring(1);
  }
  AutoId = () => {
    return this.rd4() + this.rd4() + "_" + this.rd4() + this.rd4();
  }; //********* */
  /**/
  // CreateData = () => {
  //   var tasks = [
  //     {
  //       id: this.AutoId(),
  //       name: "Hoc lap trinh",
  //       status: true
  //     },
  //     {
  //       id: this.AutoId(),
  //       name: "Hoc lap reactjs",
  //       status: true
  //     },
  //     {
  //       id: this.AutoId(),
  //       name: "Hoc lap blockchain",
  //       status: false
  //     }
  //   ];

  //   //luu data bang local
  //   this.setState({
  //     tasks: tasks
  //   });
  //   // luu vao local & chuyen ve String
  //   localStorage.setItem("tasks", JSON.stringify(tasks));
  // };
  componentWillMount() {
    if (localStorage && localStorage.getItem("tasks")) {
      var tasks = JSON.parse(localStorage.getItem("tasks"));
      this.setState({
        tasks: tasks
      });
    }
  }

  //chinh sua trang thai
  OnUpdateStatus = (id) => {
    console.log(id);
    var {tasks} = this.state;
    var index = this.findIndex(id);
    if(index !== -1){
       tasks[index].status= !tasks[index].status;
    }
  };
//duyet phan tu 
findIndex=(id)=>{
  var {tasks} = this.state;
  //duy tung phan tu
  // - 1 nghia la khong tim thay
  tasks.forEach((task,index)=>{
    if(task.id === id){
      return index;
    }
  })

}
  render() {
    var { isDisplayForm, tasks } = this.state;
    var elmTaskForm = isDisplayForm ? (
      <TaskFrom DataTaskFrom={this.DataTaskFrom} HidenFrom={this.HidenFrom} />
    ) : (
      ""
    );
    return (
      <div className={isDisplayForm ? "main" : "main2"}>
        <div className="">{elmTaskForm}</div>
        <div className="">
          <div>
            <div className="ui action input">
              <input type="text" placeholder="Search..." />
              <button className="ui button">Search</button>
            </div>
            <div className="buttonAdd">
              <button
                onClick={this.ChangeDisplay}
                className="ui primary button"
              >
                Add jobs
              </button>
              {/* <button className="ui pink button" onClick={this.CreateData}>
                Create data
              </button> */}
              <button className="ui blue button">Sort</button>
            </div>
          </div>
          <TaskFrom2 tasks={tasks} OnUpdateStatus={this.OnUpdateStatus} />
        </div>
      </div>
    );
  }
}

export default App;
