import React, { Component } from "react";
import TaskFrom from "./components/TaskFrom";
import TaskFrom2 from "./components/TaskFrom2";
import "./App.css";
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isDisplayForm: false
    };
  }

  render() {
    var { isDisplayForm } = this.state;
    var elmTaskForm = isDisplayForm ? <TaskFrom /> : "";
    return (
      <div className="main">
        <div className="">{elmTaskForm}</div>
        <div className="">
          <TaskFrom2 />
        </div>
      </div>
    );
  }
}

export default App;
