import TaskFrom from './components/TaskFrom';
import TaskFrom2 from './components/TaskFrom2';
class App extends Component {
  constructor(props) {
    super(props);
    this.state ={
        tasks:[]
    }
}

CreateData = () => {
var tasks = [
  {
    id: this.AutoId(),
    name: "Hoc lap trinh",
    status: true
  },
  {
    id: this.AutoId(),
    name: "Hoc lap reactjs",
    status: true
  },
  {
    id: this.AutoId(),
    name: "Hoc lap blockchain",
    status: false
  }
];
// console.log(tasks);
//luu data bang local
this.setState({
    tasks: tasks
})
// chuyen ve String
localStorage.setItem('staks',JSON.stringify(tasks) );
};
/* Random ID */
rd4() {
return Math.floor((1 + Math.random()) * 0x10000)
  .toString(16)
  .substring(1);
}
AutoId = () => {
return this.rd4() + this.rd4() + "_" + this.rd4() + this.rd4();
};
/**///********* */
  render() {
    return (
      <div>
        <div className="App">
          <div className="taskfrom">
            <TaskFrom />
          </div>
          <div className="taskfrom2">
            <TaskFrom2 />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
